const body=document.querySelector("body");function navigate(){const t=document.querySelector(".burger-menu a"),c=document.querySelector(".nav"),i=document.querySelectorAll(".nav__link");t.addEventListener("click",()=>{c.classList.toggle("active"),t.classList.toggle("active"),body.classList.toggle("hiden");for(var e of i)e.addEventListener("click",()=>{c.classList.remove("active"),t.classList.remove("active"),body.classList.remove("hiden")})})}function playVideo(){var e=document.querySelector(".inro__link");const t=document.querySelector(".intro");e.addEventListener("click",()=>{const e=document.createElement("div");e.classList.add("video-box"),e.innerHTML=`
                      <button class="button video__button">&#215;</button>
                      <div class="video-container">
                          <div class="video">
                              <iframe src="https://www.youtube.com/embed/JLhbTGzE6MA" title="Atlant" frameborder="0"
                                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                                  allowfullscreen>
                              </iframe>
                          </div>
                      </div>`,t.appendChild(e),body.classList.add("hiden"),e.addEventListener("click",()=>{e.remove(),body.classList.remove("hiden")})})}function imageGallery(){var t=document.querySelectorAll(".gallery__button img");const c=document.querySelector(".gallery-image__elem img"),i=document.querySelector(".gallery-image");for(let e of t)e.addEventListener("click",()=>{c.src=e.src,i.classList.add("active"),body.classList.add("hiden"),i.addEventListener("click",e=>{""!==e.target.className&&(body.classList.remove("hiden"),i.classList.remove("active"))})})}navigate(),playVideo(),imageGallery();